package ch2;

public class OperEx1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b, c;
		a = 20;
		b = 7;
		c = a + b;
		System.out.println("a + b = " + c);
		
		c = a - b;
		System.out.println("a - b = " + c);
		
		c = a % b;
		System.out.println("a % b = " + c);
	}

}
