package ch2;

public class OperEx3 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 7;
		
		System.out.println(a + b);
		System.out.println(" 변수 a : " + a);
		
		System.out.println(a += b); // a = a + b;와 같다.
		System.out.println(" 변수 a : " + a);
	}

}
