package ch2;

public class OperEx7 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 12;
		int b = 2;
		
		int c = a >> b;
		System.out.println("변수 c의 값 : " + c);
		
		char d = (char)(a << 2);
		System.out.println("변수 d의 값 : " + d);
	}

}
